import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const NewsCard = ({ news, user, onDelete }) => {
  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this news post?')) return;
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/news/${news._id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      onDelete(news._id);
      alert('News post deleted successfully');
    } catch (err) {
      console.error(err);
      alert('Failed to delete news post. Please try again.');
    }
  };

  return (
    <div className={`p-4 rounded-lg shadow-md ${news.isBreaking ? 'bg-red-100' : 'bg-white'} hover:shadow-lg transition`}>
      <h3 className="text-xl font-semibold text-neutral">{news.title}</h3>
      <p className="text-gray-600 mt-2">{news.content.substring(0, 100)}...</p>
      {news.isBreaking && <span className="text-accent font-medium">Breaking News</span>}
      <div className="mt-4 flex space-x-4">
        <Link to={`/news/${news._id}`} className="text-primary hover:underline">Read More</Link>
        {user && (
          <button
            onClick={handleDelete}
            className="bg-accent text-white px-3 py-1 rounded hover:bg-red-700 transition"
          >
            Delete
          </button>
        )}
      </div>
    </div>
  );
};

export default NewsCard;
