import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CommentSection = ({ newsId, user }) => {
  const [comments, setComments] = useState([]);
  const [content, setContent] = useState('');

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_URL}/comments/${newsId}`)
      .then(res => setComments(res.data))
      .catch(err => console.error(err));
  }, [newsId]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL}/comments`,
        { newsId, content },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setContent('');
      window.location.reload();
    } catch (err) {
      console.error(err);
      alert('Failed to post comment. Please try again.');
    }
  };

  return (
    <div className="mt-8">
      <h3 className="text-xl font-semibold text-neutral mb-4">Comments</h3>
      {comments.map(comment => (
        <div key={comment._id} className="border-b border-gray-200 py-4">
          <p className="text-gray-600">
            <strong className="text-neutral">{comment.userId.username}</strong>: {comment.content}
          </p>
        </div>
      ))}
      {user ? (
        <form onSubmit={handleSubmit} className="mt-4">
          <textarea
            placeholder="Add a comment"
            value={content}
            onChange={e => setContent(e.target.value)}
            required
            className="w-full p-2 border rounded-md mb-4 focus:outline-none focus:ring-2 focus:ring-primary"
            rows="4"
          />
          <button
            type="submit"
            className="bg-primary text-white px-4 py-2 rounded-md hover:bg-blue-700 transition"
          >
            Post Comment
          </button>
        </form>
      ) : (
        <p className="text-gray-600">
          Please <Link to="/auth" className="text-primary hover:underline">login</Link> to post a comment.
        </p>
      )}
    </div>
  );
};

export default CommentSection;
