import React, { useState } from 'react';
import axios from 'axios';

const NewsForm = ({ user }) => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [isBreaking, setIsBreaking] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        `${process.env.REACT_APP_API_URL}/news`,
        { title, content, isBreaking },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setTitle('');
      setContent('');
      setIsBreaking(false);
      window.location.reload();
    } catch (err) {
      console.error(err);
      alert('Failed to post news. Please try again.');
    }
  };

  if (!user) return null;

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-lg shadow-md mb-8">
      <h2 className="text-2xl font-semibold text-neutral mb-4">Post News</h2>
      <input
        type="text"
        placeholder="Title"
        value={title}
        onChange={e => setTitle(e.target.value)}
        required
        className="w-full p-2 border rounded-md mb-4 focus:outline-none focus:ring-2 focus:ring-primary"
      />
      <textarea
        placeholder="Content"
        value={content}
        onChange={e => setContent(e.target.value)}
        required
        className="w-full p-2 border rounded-md mb-4 focus:outline-none focus:ring-2 focus:ring-primary"
        rows="5"
      />
      <label className="flex items-center mb-4">
        <input
          type="checkbox"
          checked={isBreaking}
          onChange={e => setIsBreaking(e.target.checked)}
          className="mr-2"
        />
        <span className="text-gray-600">Mark as Breaking News</span>
      </label>
      <button
        type="submit"
        className="bg-primary text-white px-4 py-2 rounded-md hover:bg-blue-700 transition"
      >
        Post News
      </button>
    </form>
  );
};

export default NewsForm;
