import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Header = ({ user, setUser }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <header className="bg-neutral text-white py-4 px-6 flex justify-between items-center shadow-md">
      <h1 className="text-2xl font-bold">News App</h1>
      <nav className="flex items-center space-x-4">
        <Link to="/" className="hover:text-primary transition">Home</Link>
        {user ? (
          <>
            <Link to="/profile" className="hover:text-primary transition">Profile</Link>
            <span className="text-sm">Welcome, {user.username}</span>
            <button
              onClick={handleLogout}
              className="bg-primary text-white px-3 py-1 rounded hover:bg-blue-700 transition"
            >
              Logout
            </button>
          </>
        ) : (
          <Link to="/auth" className="hover:text-primary transition">Login/Signup</Link>
        )}
      </nav>
    </header>
  );
};

export default Header;
