import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Profile = ({ user, setUser }) => {
  const navigate = useNavigate();

  const handleDeleteAccount = async () => {
    if (!window.confirm('Are you sure you want to delete your account? This cannot be undone.')) return;
    try {
      await axios.delete(`${process.env.REACT_APP_API_URL}/auth/me`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setUser(null);
      localStorage.removeItem('token');
      navigate('/');
      alert('Account deleted successfully');
    } catch (err) {
      console.error(err);
      alert('Failed to delete account. Please try again.');
    }
  };

  if (!user) return <div className="text-center text-gray-600">Please log in to view your profile.</div>;

  return (
    <div className="bg-white p-6 rounded-lg shadow-md max-w-md mx-auto mt-8">
      <h2 className="text-2xl font-semibold text-neutral mb-4">Profile</h2>
      <p className="text-gray-600 mb-2"><strong>Username:</strong> {user.username}</p>
      <p className="text-gray-600 mb-4"><strong>Email:</strong> {user.email}</p>
      <button
        onClick={handleDeleteAccount}
        className="bg-accent text-white px-4 py-2 rounded-md hover:bg-red-700 transition"
      >
        Delete Account
      </button>
    </div>
  );
};

export default Profile;
